sub EndsWithTextExtension {
	my ($text) = @_;
	return($text =~ $textExtensions);
	}

sub EndsWithImageExtension {
	my ($text) = @_;
	return($text =~ $imageExtensions);
	}

sub EndsWithTextOrImageExtension {
	my ($text) = @_;
	return($text =~ $textExtensions || $text =~ $imageExtensions);	
	}
	
sub GetTextExtension {
	my ($text) = @_;
	my $result = ($text =~ $textExtensions) ? $1: '';
	return($result);
	}

sub GetImageExtension {
	my ($text) = @_;
	my $result = ($text =~ $imageExtensions) ? $1: '';
	return($result);
	}

sub GetTextOrImageExtension {
	my ($text) = @_;
	my $result = ($text =~ $textExtensions || $text =~ $imageExtensions) ? $1: '';
	return($result);
	}
	
