# Total number of (unique) extensions, to all determining if all extensions have been selected.
# As of last count there are 226.
sub NumExtensions {
	my %uniqueExtensions;
	foreach my $key (keys %extensionsForLanguage)
		{
		my $value = $extensionsForLanguage{$key};
		my @exts = split(/,/, $value);
		for (my $i = 0; $i < @exts; ++$i)
			{
			$uniqueExtensions{$exts[$i]} += 1;
			}
		}
	
	my $result = keys %uniqueExtensions;
	
	return($result);
	}

